import React, { useState, useEffect } from 'react';
import { Grid, Card, CardMedia, CardContent, Typography } from '@mui/material';
import { useLocation } from 'react-router-dom';
import { Buffer } from 'buffer';
import { fetchDeleteDataWithAuth, fetchGetDataWithAuth, fetchGetDataWithAuthArrayBuffer, fetchGetBlobDataWithAuth } from 'client/Client';
import { Tooltip } from '../../../node_modules/@mui/material/index';
import { makeStyles } from '@mui/styles';
import Modal from '@mui/material/Modal';
import Button from '@mui/material/Button';

const useStyles = makeStyles((theme) => ({
  modal: {
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center'
  },
  modalMain: {
    backgroundColor: theme.palette.background.paper,
    borderRadius: '10px',
    boxShadow: theme.shadows[5],
    padding: theme.spacing(2, 4, 3),
    maxHeight: '90%',
    maxWidth: '90%',
    overflow: 'auto'
  },
  closeButton: {
    marginLeft: 'auto'
  }
}));
// ==============================|| PHOTO GRID ||============================== //

const PhotoGrid = () => {
  const [photos, setPhotos] = useState({});
  const location = useLocation();
  const queryParams = new URLSearchParams(location.search);
  const album_id = queryParams.get('id');
  const [albumInfo, setAlbumInfo] = useState({});
  const classes = useStyles();
  const [open, setOpen] = useState(false);
  const [PhotoDesc, setPhotoDesc] = useState(null);
  const [PhotoContent, setPhotoContent] = useState(null);
  const [DownloadLink, setDownloadLink] = useState(null);

  const handleOpen = () => {
    setOpen(true);
  };

  const handleClose = () => {
    setOpen(false);
  };

  const handleView = (download_link, description) => {
    fetchGetDataWithAuthArrayBuffer(download_link).then((response) => {
      const buffer = Buffer.from(new Uint8Array(response.data)).toString('base64');
      setPhotoContent(buffer);
    });

    handleOpen();
    setDownloadLink(download_link);
    setPhotoDesc(description);
  };
  const handleDownload = (download_link) => {
    console.log(download_link);
    fetchGetBlobDataWithAuth(download_link)
      .then((response) => {
        console.log(response);

        const disposition = response.headers.get('Content-Disposition');
        const match = /filename="(.*)"/.exec(disposition);

        const filename = match ? match[1] : 'downloadedFile';

        const url = window.URL.createObjectURL(new Blob([response.data]));
        const link = document.createElement('a');
        link.href = url;
        link.setAttribute('download', filename);
        document.body.appendChild(link);
        link.click();
      })
      .catch((error) => {
        console.log('Error downloadinng photo: ', error);
      });
  };

  const handleDelete = (photo_id) => {
    const isConfirmed = window.confirm('Are you sure you want to delete the photo?');

    if (isConfirmed) {
      console.log('Item deleted!');
      fetchDeleteDataWithAuth('/albums/' + album_id + '/photos/' + photo_id + '/delete-photo').then((res) => {
        console.log(res);
        window.location.reload();
      });
    } else {
      console.log('Delte operation cancelled!');
    }
  };

  useEffect(() => {
    fetchGetDataWithAuth('/albums/' + album_id).then((res) => {
      setAlbumInfo(res.data);
      const photosList = res.data.photos;

      photosList.forEach((photo) => {
        let thumbnail_link = photo.download_link.replace('/download-photo', '/download-thumbnail');
        fetchGetDataWithAuthArrayBuffer(thumbnail_link).then((response) => {
          const albumPhotoId = 'album_' + album_id + '_photo_' + photo.id;

          const buffer = Buffer.from(new Uint8Array(response.data)).toString('base64');
          console.log(buffer);
          const temp = {
            album_id: album_id,
            photo_id: photo.id,
            name: photo.name,
            description: photo.description,
            content: buffer,
            download_link: photo.download_link
          };

          setPhotos((prevPhotos) => ({ ...prevPhotos, [albumPhotoId]: temp }));
        });
      });
    });
  }, [album_id]); 

  return (
    <div>
      <Modal
        open={open}
        onClose={handleClose}
        aria-labelledby="modal-modal-title"
        aria-describedby="modal-modal-description"
        className={classes.modal}
      >
        <div className={classes.modalMain}>
          <img src={'data:image/jpeg;base64,' + PhotoContent} alt={PhotoDesc} style={{ width: '100%', height: 'auto' }} />
          <Button onClick={() => handleDownload(DownloadLink)}> Download </Button>
          <Button onClick={handleClose} className={classes.closeButton}>
            {' '}
            Close{' '}
          </Button>
        </div>
      </Modal>
      {/*TO display album name & desc. gutterBottom will add some space  */}
      <Typography variant="h4" gutterBottom>
        {albumInfo.name}
      </Typography>
      <Typography variant="subtitle1" gutterBottom>
        {albumInfo.description}
      </Typography>
      <Grid container spacing={2}>
        {Object.keys(photos).map((key) => (
          <Grid item xs={12} sm={6} md={4} lg={2} key={key}>
            <Card>
              <Tooltip title={photos[key]['description']}>
                <CardMedia
                  component="img"
                  height="140"
                  image={`data:image/jpeg;base64,${photos[key]['content']}`}
                  alt={photos[key]['description']}
                />
              </Tooltip>
              <CardContent>
                <Tooltip title={photos[key]['description']}>
                  <Typography variant="subtitle1" noWrap>
                    {photos[key]['name']}
                  </Typography>
                </Tooltip>
                <a href="#" onClick={() => handleView(photos[key]['download_link'], photos[key]['description'])}>
                  View
                </a>
                ||
                <a
                  href={`/photo/edit?album_id=${album_id}&photo_id=${photos[key]['photo_id']}&photo_name=${photos[key]['name']}&photo_description=${photos[key]['description']}`}
                >
                  Edit
                </a>
                ||
                <br></br>
                <a href="#" onClick={() => handleDownload(photos[key]['download_link'])}>
                  Download
                </a>
                ||
                <a href="#" onClick={() => handleDelete(photos[key]['photo_id'])}>
                  Delete
                </a>
              </CardContent>
            </Card>
          </Grid>
        ))}
      </Grid>
    </div>
  );
};

export default PhotoGrid;
